mapper.select:

SELECT *
FROM (
  SELECT d.*, ROWNUM rn
  FROM ( 
    SELECT * 
    FROM DIARYS 
    ORDER BY CREATED_AT DESC 
    ) d
  WHERE ROWNUM <= #{endRowNo}
 )
 WHERE rn >= #{startRowNo}

mapper.update:
UPDATE DIARYS
SET TITLE = #{title}, CONTENT = #{content}, EMO = #{emo}, WEATHER = #{weather}, VIEW_SCOPE = #{viewScope}, UPDATED_AT = SYSTIMESTAMP
WHERE DID = #{did}

mapper.delete:
DELETE FROM DIARYS
WHERE DID IN

  #{did}
